<?php/*** CG Avis Client - Joomla Component * Package			: Joomla 4.x/5.x/6.x* copyright 		: Copyright (C) 2025 ConseilGouz. All rights reserved.* license    		: https://www.gnu.org/licenses/gpl-3.0.html GNU/GPL* From              : OT Testimonies  version 1.0, OmegaTheme Extensions - http://omegatheme.com*/namespace ConseilGouz\Component\CGAvisClient\Site\View\Item;defined('_JEXEC') or die;use Joomla\CMS\Factory;use Joomla\CMS\MVC\View\GenericDataException;use Joomla\CMS\MVC\View\HtmlView as BaseHtmlView;use Joomla\CMS\Uri\Uri;class HtmlView extends BaseHtmlView {	protected $items;	protected $pagination;    protected $state;    protected $return_page;	function display($tpl = null)	{        $app = Factory::getApplication();         $params = $app->getParams();                  $this->form        = $this->get('Form');  		$this->items		= $this->get('Items');		$this->pagination 	= false;          $this->freetext = $params->get('freetext');              $this->layout =  $params->get('cgavisclient_layout');		$this->return = $params->get('returnmenu');        $this->params = $params;   		// Check for errors.		if (count($errors = $this->get('Errors'))) {			\Error::raiseError(500, implode("\n", $errors));			return false;		}         $document = Factory::getApplication()->getDocument();         $wa = $document->getWebAssetManager();		$wa->registerAndUseStyle('fontawesome','media/system/css/joomla-fontawesome.min.css');        $wa->registerAndUseStyle('aviscss','media/com_cgavisclient/css/style.css');        $this->setLayout($this->_layout);    		parent::display($tpl);	}	}
